package pages;import com.microsoft.playwright.Locator;import com.microsoft.playwright.Page;import com.microsoft.playwright.Playwright;import io.qameta.allure.Step;public class HomePage extends BasePage{    private final String HOME_PAGE_TITLE = "Hands-On Selenium WebDriver with Java";    Page page;        public HomePage(Page page, Playwright  playwright) {        super( page, playwright);    }        public String getHeaderText() {        return page.textContent("h1");    }        @Step("Open homepage")    private void openHome() {        page.navigate(BASE_URL);    }        @Step("Open Web form page")    public WebFormPage getWebFormPageHeading() {        page.locator("css=.display-6.heading");        return new WebFormPage(page);    }        @Override    public String getUrl() {        return BASE_URL;    }    @Override    public String getTitle() {        return HOME_PAGE_TITLE;    }}