package pages;import com.microsoft.playwright.Locator;import com.microsoft.playwright.Page;import com.microsoft.playwright.Playwright;import com.microsoft.playwright.options.AriaRole;public class LocatorsElements extends BasePage {        private final String HOME_PAGE_TITLE = "Hands-On Selenium WebDriver with Java";        protected LocatorsElements( Page page,Playwright playwright ){        super( page,playwright );    }        public Locator heading (String text) {        return getPage().getByRole( AriaRole.HEADING, new Page.GetByRoleOptions().setName(text));    }        @Override    public String getUrl( ){        return BASE_URL;    }        @Override    public String getTitle( ){        return HOME_PAGE_TITLE;    }}