package base;import com.microsoft.playwright.*;import com.microsoft.playwright.options.AriaRole;import org.testng.annotations.AfterMethod;import org.testng.annotations.BeforeMethod;public class BaseTest {    public static final String BASE_URL = "https://bonigarcia.dev/selenium-webdriver-java/";    Playwright playwright = Playwright.create();    protected Browser browser = playwright            .chromium()            .launch(new BrowserType.LaunchOptions()                    .setHeadless(false));    BrowserContext context = browser.newContext();    Page page = context.newPage();        public void open () {        page.navigate(BASE_URL);    }        public void hoverMenu(String menu) {        getLink(menu).hover();    }        public void clickMenu(String menu) {        getLink(menu).click();    }        public Locator getLink(String text) {        return page.getByRole( AriaRole.LINK, new Page.GetByRoleOptions().setName(text));    }        @BeforeMethod    public void setup() {        playwright = Playwright.create();        browser = playwright.chromium().launch(new BrowserType.LaunchOptions().setHeadless(true));        context = browser.newContext();        page = context.newPage();    }        @AfterMethod    public void tearDown() {        page.close();        context.close();        browser.close();        playwright.close();    }    }