package uitests;import com.microsoft.playwright.Browser;import com.microsoft.playwright.BrowserType;import com.microsoft.playwright.Page;import com.microsoft.playwright.Playwright;import org.testng.annotations.AfterClass;import org.testng.annotations.BeforeClass;import org.testng.annotations.Test;public class HomePageTest{    Playwright playwright;    Browser browser;    Page page;        @BeforeClass    public void setUp() {        playwright = Playwright.create();        browser = playwright.chromium().launch(new BrowserType.LaunchOptions().setHeadless(true));        page = browser.newPage();    }        @Test    public void testPageTitle() {        page.navigate("https://bonigarcia.dev/selenium-webdriver-java/");        String title = page.title();        assert title.contains("Selenium WebDriver");    }        @AfterClass    public void tearDown() {        browser.close();        playwright.close();    }}